frappe.ui.form.on('Tractor Inspection Checksheet', {
    refresh: function(frm) {
        if (frm.doc.__islocal) {
            // Load abnormalities table
            if (!frm.doc.abnormalities_log || frm.doc.abnormalities_log.length === 0) {
                add_abnormality_rows(frm);
            }

            // Load frequency-specific items
            if (frm.doc.frequency && (!frm.doc.inspection_items || frm.doc.inspection_items.length === 0)) {
                load_inspection_items(frm);
            }
        }
    },

    frequency: function(frm) {
        if (frm.doc.frequency) {
            frm.clear_table('inspection_items');
            load_inspection_items(frm);
        }
    }
});

function add_abnormality_rows(frm) {
    const rows = [
        { week: "Abnormalities identified" },
        { week: "Abnormalities corrected" }
    ];
    rows.forEach(item => {
        const row = frm.add_child('abnormalities_log');
        row.week = item.week;
        row.identified = 0;
        row.corrected = 0;
    });
    frm.refresh_field('abnormalities_log');
}

function load_inspection_items(frm) {
    const inspection_items = get_inspection_items_by_type(frm.doc.frequency);

    inspection_items.forEach(item => {
        const row = frm.add_child('inspection_items');
        row.data = item.item;
        row.notes = item.action_required;
        row.status = '- Not Checked';
        row.priority = item.priority || 'Medium';
    });

    frm.refresh_field('inspection_items');
}

function get_inspection_items_by_type(frequency) {
    const items = {
        'Daily': [
            { item: "Engine Oil Level", action_required: "Top up if low", priority: "High" },
            { item: "Coolant Level", action_required: "Top up if low", priority: "High" },
            { item: "Hydraulic Fluid Level", action_required: "Top up if low", priority: "High" },
            { item: "Transmission Fluid Level", action_required: "Top up if low", priority: "High" },
            { item: "Fuel Level", action_required: "Refuel if low", priority: "High" },
            { item: "Lights and Indicators", action_required: "Replace bulbs if needed", priority: "Medium" },
            { item: "Belts and Hoses", action_required: "Replace if necessary", priority: "High" },
            { item: "Tires and Wheels", action_required: "Adjust pressure or replace if needed", priority: "High" }
        ],

        'Weekly': [
            { item: "Air Filter", action_required: "Replace if necessary", priority: "Medium" },
            { item: "Battery Condition", action_required: "Clean or replace if needed", priority: "High" },
            { item: "Fuel Filter", action_required: "Replace if necessary", priority: "Medium" },
            { item: "Grease Points", action_required: "Lubricate if needed", priority: "Medium" }
        ],

        'Monthly': [
            { item: "Radiator and Cooling System", action_required: "Repair or replace if necessary", priority: "High" },
            { item: "Steering and Suspension", action_required: "Investigate if necessary", priority: "High" },
            { item: "Exhaust System", action_required: "Address issues if found", priority: "Medium" },
            { item: "Transmission Filter", action_required: "Replace if needed", priority: "Medium" },
            { item: "Brake System", action_required: "Replace or top up fluid if needed", priority: "Critical" }
        ],

        'Quarterly': [
            { item: "Battery Electrolyte Level", action_required: "Top up with distilled water", priority: "High" },
            { item: "Drive Belts", action_required: "Replace if needed", priority: "High" },
            { item: "Fuel Lines and Connections", action_required: "Repair or replace if necessary", priority: "High" }
        ],

        'Yearly': [
            { item: "Engine Compression", action_required: "Investigate and repair if necessary", priority: "High" },
            { item: "Cooling System Flush", action_required: "Replace if necessary", priority: "High" },
            { item: "Transmission Service", action_required: "Replace if needed", priority: "High" },
            { item: "Brake Fluid Replacement", action_required: "Replace if needed", priority: "Critical" }
        ]
    };

    return items[frequency] || [];
}

frappe.ui.form.on('CFU Inspection Checksheet', {
    refresh: function(frm) {
        if (frm.doc.__islocal) {
            // Add abnormalities table rows if not already added
            if (!frm.doc.abnormalities_log || frm.doc.abnormalities_log.length === 0) {
                add_cfu_abnormality_rows(frm);
            }

            // Load inspection items if frequency is already selected
            if (frm.doc.frequency && (!frm.doc.inspection_items || frm.doc.inspection_items.length === 0)) {
                load_cfu_items(frm);
            }
        }
    },

    frequency: function(frm) {
        if (frm.doc.frequency) {
            frm.clear_table('inspection_items');
            load_cfu_items(frm);
        }
    }
});

// Add abnormalities rows
function add_cfu_abnormality_rows(frm) {
    const rows = [
        { week: "Abnormalities identified", identified: 0, corrected: 0 },
        { week: "Abnormalities corrected", identified: 0, corrected: 0 }
    ];
    rows.forEach(row => {
        const child = frm.add_child('abnormalities_log');
        child.week = row.week;
        child.identified = row.identified;
        child.corrected = row.corrected;
    });
    frm.refresh_field('abnormalities_log');
}

// Load inspection items for selected frequency
function load_cfu_items(frm) {
    const frequency = frm.doc.frequency;
    const checklist = get_cfu_items_by_frequency(frequency);

    checklist.forEach(item => {
        const row = frm.add_child('inspection_items');
        row.equipment = item.equipment;
        row.part_name = item.part_name;
        row.parameter_checked = item.parameter_checked;
        row.status = "Not Checked";
        row.notes = item.notes || "";
    });

    frm.refresh_field('inspection_items');
}

