frappe.ui.form.on('Packhouse Equipment and Machine AM Checklist', {
    refresh: function(frm) {
        if (frm.doc.__islocal && frm.doc.equipment_type) {
            if (!frm.doc.inspection_items || frm.doc.inspection_items.length === 0) {
                load_equipment_items(frm);
            }
        }
    },

    equipment_type: function(frm) {
        frm.clear_table('inspection_items');
        load_equipment_items(frm);
    }
});

function load_equipment_items(frm) {
    const items = get_items_by_equipment_type(frm.doc.equipment_type);

    items.forEach(item => {
        const row = frm.add_child('inspection_items');
        row.equipment = item.equipment;
        row.part_name = item.part_name;
        row.parameter_checked = item.parameter_checked;
        row.status = "Not Checked";
        row.notes = item.notes || "";
    });

    frm.refresh_field('inspection_items');
}

