frappe.ui.form.on('Harvest', {
    validate: function(frm) {
        var field_value = frm.doc.bay;
        if (field_value) {
            // Convert to uppercase and set it back
            field_value = field_value.toUpperCase();
            frm.set_value('bay', field_value);

            // Check if it contains only alphabets and spaces
            if (!/^[A-Z\s]+$/.test(field_value)) {
                frappe.msgprint(__('The field {0} can only contain alphabets.'), [frm.fields_dict.bay.label]);
                frappe.validated = false; // Prevent saving the form
            }
        }
    },
    bay: function(frm) {
        var field_value = frm.doc.bay;
        if (field_value) {
            // Convert to uppercase and remove non-alphabetic characters
            var cleaned_value = field_value.toUpperCase().replace(/[^A-Z\s]/g, '');
            if (field_value !== cleaned_value) {
                frm.set_value('bay', cleaned_value);
                frappe.show_alert('Only uppercase alphabets are allowed in this field.');
            }
        }
    }
});

// Custom Script for Material Request
