frappe.ui.form.on('Material Request', {
    refresh: function(frm) {
        // Show Repack button only for Material Transfer requests that are submitted
        if (frm.doc.material_request_type === 'Material Transfer' && 
            frm.doc.docstatus === 1) {

            frm.add_custom_button(__('Repack'), function() {
                create_repack_stock_entry(frm);
            }, __('Create'));

            // Set button color to indicate it's a primary action
            frm.page.btn_primary.find('.btn-group .btn:contains("Repack")').addClass('btn-primary');
        }
    }
});

function create_repack_stock_entry(frm) {
    // Validate that there are items in the request
    if (!frm.doc.items || frm.doc.items.length === 0) {
        frappe.msgprint(__('No items found in Material Request'));
        return;
    }

    // Show loading indicator
    frappe.show_alert({
        message: __('Creating Stock Entry...'),
        indicator: 'blue'
    });

    // Call server method to create Stock Entry
    frappe.call({
        method: 'create_repack_entry', // API Method defined in the Server Script
        args: {
            material_request: frm.doc.name, // Replace with the actual Material Request name
        },
        callback: function(response) {
            if (response.message) {
                frappe.show_alert({
                    message: __('Stock Entry created successfully: {0}', [response.message]),
                    indicator: 'green'
                });

                // Redirect to the new Stock Entry
                frappe.set_route('Form', 'Stock Entry', response.message);
            }
        },
        error: function(error) {
            frappe.show_alert({
                message: __('Error creating Stock Entry'),
                indicator: 'red'
            });
            console.error('Error creating Stock Entry:', error);
        }
    });


}



