frappe.ui.form.on('Delivery Trip', {
    refresh: function(frm) {
        if (frm.doc.status === "Scheduled" && !frm.doc.start_time) {
            frm.add_custom_button(('Start Trip'), function () {
                frappe.call({
                    method: "start_trip_transfer",
                    args: {
                        delivery_trip: frm.doc.name,
                    },
                    callback: function() {
                        frm.reload_doc();
                        frappe.msgprint("Trip started and stock moved to Goods In Transit.");
                    }
                });
            });
        }

        if (frm.doc.status === "In Transit" && !frm.doc.end_time) {
            frm.add_custom_button(('End Trip'), function () {
                frappe.call({
                    method: "end_trip_transfer",
                    args: {
                        delivery_trip: frm.doc.name
                    },
                    callback: function() {
                        frm.reload_doc();
                        frappe.msgprint("Trip completed and stock moved to Karen Yoghurt Goods.");
                    }
                });
            });
        }
    }
});

frappe.ui.form.on('Work Order', {
    production_item: function(frm) {
        if (!frm.doc.production_item) return;

        frappe.db.get_doc('Item', frm.doc.production_item).then(item_doc => {
            if (item_doc.default_warehouse) {
                frappe.db.get_doc('Warehouse', item_doc.default_warehouse).then(warehouse_doc => {
                    if (warehouse_doc.company && frm.doc.company !== warehouse_doc.company) {
                        frm.set_value('company', warehouse_doc.company);
                    }
                });
            }
        });
    }
});

frappe.ui.form.on('Work Order', {
    bom_no: function (frm) {
        if (!frm.doc.bom_no) return;

        frappe.call({
            method: "frappe.client.get",
            args: {
                doctype: "BOM",
                name: frm.doc.bom_no
            },
            callback: function (res) {
                const bom = res.message;
                if (bom && bom.company) {
                    frm.set_value("company", bom.company);
                }
            }
        });
    }
});

frappe.ui.form.on('Work Order', {
    bom_no: function (frm) {
        if (!frm.doc.bom_no) return;

        frappe.db.get_doc('BOM', frm.doc.bom_no).then(bom_doc => {
            if (bom_doc.company && frm.doc.company !== bom_doc.company) {
                frm.set_value('company', bom_doc.company);
            }
        });
    }
});

frappe.ui.form.on('Work Order', {
    // Trigger logic when BOM is manually selected
    bom_no: function (frm) {
        set_warehouses_based_on_bom(frm);
    },

    // Trigger logic when the form is loaded and BOM is already set
    onload: function (frm) {
        if (frm.doc.bom_no) {
            set_warehouses_based_on_bom(frm);
        }
    }
});

// Extracted function so it's reusable
async function set_warehouses_based_on_bom(frm) {
    if (!frm.doc.bom_no) return;

    const { message: bom } = await frappe.db.get_value('BOM', frm.doc.bom_no, 'item');
    if (!bom || !bom.item) return;

    const item_name = bom.item.toLowerCase();

    frm.set_value('wip_warehouse', 'Pasteurization Unit - KR');

    if (item_name.includes('lala')) {
        frm.set_value('fg_warehouse', 'Finished Goods - KR');
    } else if (item_name.includes('vanilla yoghurt') || item_name.includes('strawberry yoghurt')) {
        frm.set_value('fg_warehouse', 'Yogurt Coldroom - KR');
    } else {
        frm.set_value('fg_warehouse', '');
    }
}

frappe.ui.form.on('Work Order', {
    bom_no: async function (frm) {
        if (!frm.doc.bom_no) return;

        // Step 1: Fetch BOM details
        const bom_doc = await frappe.db.get_doc('BOM', frm.doc.bom_no);
        if (!bom_doc || !bom_doc.item) return;

        const item_name = bom_doc.item.toLowerCase();

        // Step 2: If BOM has a different company, set it first
        if (bom_doc.company && frm.doc.company !== bom_doc.company) {
            await frm.set_value('company', bom_doc.company);
        }

        // Step 3: Set WIP and Target warehouses based on item
        frm.set_value('wip_warehouse', 'Pasteurization Unit - KR');

        if (item_name.includes('maziwa lala (kg)')) {
            frm.set_value('fg_warehouse', 'Yoghurt/Lala Packing - KR');
        } else if (item_name.includes('yoghurt vanilla (kg)') || item_name.includes('yoghurt strawberry (kg)')) {
            frm.set_value('fg_warehouse', 'Yoghurt/Lala Packing - KR');
        } else {
            frm.set_value('fg_warehouse', '');
        }
    },

    item_to_manufacture: async function (frm) {
        if (!frm.doc.item_to_manufacture) return;

        // Fetch the item name
        const item_name = frm.doc.item_to_manufacture.toLowerCase();

        // Set Target Warehouse based on item
        if (item_name.includes('maziwa lala (kg)')) {
            frm.set_value('fg_warehouse', 'Yoghurt/Lala Packing - KR');
        } else if (item_name.includes('yoghurt vanilla (kg)') || item_name.includes('yoghurt strawberry (kg)')) {
            frm.set_value('fg_warehouse', 'Yoghurt/Lala Packing - KR');
        } else {
            frm.set_value('fg_warehouse', '');
        }
    },

    onload: function (frm) {
        if (frm.doc.bom_no) {
            frappe.ui.form.trigger('Work Order', 'bom_no', frm);
        }
    }
});

frappe.ui.form.on('Sales Order', {
    custom_business_unit(frm) {
        if (frm.doc.custom_business_unit == "Westwood Yoghurt") {
            frm.doc.set_warehouse = "Yoghurt Store Karen - KR";
            frm.refresh_field('set_warehouse');
        }
    }
});

frappe.ui.form.on('Stock Entry', {
    stock_entry_type: async function (frm) {
        if (!frm.doc.stock_entry_type) return;

        // Fetch selected Stock Entry Type document
        const stock_entry_type = await frappe.db.get_doc('Stock Entry Type', frm.doc.stock_entry_type);

        if (!stock_entry_type) return;

        // Set custom values if available
        if (stock_entry_type.custom_default_farm) {
            frm.set_value('custom_farm', stock_entry_type.custom_default_farm);
        }

        if (stock_entry_type.custom_default_business_unit) {
            frm.set_value('custom_business_unit', stock_entry_type.custom_default_business_unit);
        }

        // if (stock_entry_type.custom_default_company && frm.doc.company !== stock_entry_type.custom_default_company) {
        //     frm.set_value('company', stock_entry_type.custom_default_company);
        // }
    },

    // BONUS: Run on form load if Stock Entry Type is already selected
    onload: function (frm) {
        if (frm.doc.stock_entry_type) {
            frappe.ui.form.trigger('Stock Entry', 'stock_entry_type', frm);
        }
    }
});

frappe.ui.form.on('Job Card', {
    onload: function (frm) {
        enforce_quality_inspection_rule(frm);
    },
    refresh: function (frm) {
        enforce_quality_inspection_rule(frm);
    },
    validate: function (frm) {
        if (frm.doc.operation === "Milk Quality Inspection" && frm.doc.status === "Completed") {
            if (!frm.doc.quality_inspection || !frm.doc.custom_quality_inspection_submitted) {
                frappe.throw(__('You cannot complete the job without submitting a Quality Inspection for the milk.'));
            }
        }
    }
});

// Helper function to enforce the quality inspection rule
function enforce_quality_inspection_rule(frm) {
    if (frm.doc.operation === "Milk Quality Inspection") {
        frm.fields_dict.quality_inspection.$wrapper.find('button').off('click').on('click', function (e) {
            if (frm.doc.status !== "Work In Progress") {
                e.preventDefault();
                frappe.msgprint(__('Please start the job before creating or selecting a Quality Inspection.'));
            }
        });
    }
}

frappe.ui.form.on('Stock Entry', {
    stock_entry_type: function (frm) {
        const autoTypes = ["Milk to Yoghurt BU", "Milking"];
        const source_warehouse = "Milk Cooler - KR";
        const target_warehouse = "Pasteurization Unit - KR";

        if (autoTypes.includes(frm.doc.stock_entry_type)) {
            (frm.doc.items || []).forEach(item => {
                frappe.model.set_value(item.doctype, item.name, 's_warehouse', source_warehouse);
                frappe.model.set_value(item.doctype, item.name, 't_warehouse', target_warehouse);
            });
        }
