frappe.ui.form.on('Harvest', {
    refresh(frm) {
        frm.add_custom_button("Scan QR Code", () => {
            checkAndStartScanner();
        });

        function checkAndStartScanner() {
            const { farm, block } = frm.doc;

            if (!farm || !block) {
                return;
            }

            if (!window.Html5Qrcode) {
                const script = document.createElement("script");
                script.src = "https://cdnjs.cloudflare.com/ajax/libs/html5-qrcode/2.3.8/html5-qrcode.min.js";
                script.onload = () => {
                    console.log("Html5Qrcode library loaded successfully.");
                    showQrScannerDialog();
                };
                script.onerror = () => {
                    console.error("Failed to load Html5Qrcode library.");
                    frappe.msgprint("Failed to load QR scanning library. Please check your network connection.");
                };
                document.head.appendChild(script);
            } else {
                showQrScannerDialog();
            }
        }

        function showQrScannerDialog() {
            const dialog = new frappe.ui.Dialog({
                title: "Scan QR Code",
                fields: [
                    { fieldname: 'qr_scanner', fieldtype: 'HTML', options: '' }
                ]
            });

            const $wrapper = dialog.fields_dict.qr_scanner.$wrapper;
            $wrapper.html('<div id="qr-reader" style="width: 100%;"></div>');

            dialog.show();

            setTimeout(() => {
                const qrReaderElement = document.getElementById("qr-reader");
                if (qrReaderElement) {
                    initializeQrScanner(qrReaderElement, dialog);
                } else {
                    console.error("QR Reader element not found!");
                    frappe.msgprint("Error initializing QR scanner. Please try again.");
                }
            }, 300);
        }

        let isProcessing = false;

        function initializeQrScanner(qrReaderElement, dialog) {
            const html5QrCode = new Html5Qrcode("qr-reader");

            html5QrCode.start(
                { facingMode: "environment" },
                {
                    fps: 15,
                    qrbox: { width: 250, height: 250 }
                },
                async (decodedText, decodedResult) => {
                    if (isProcessing) return;
                    isProcessing = true;

                    try {

                        const parsed = JSON.parse(decodedText);
                        const keys_array = Object.keys(parsed);
                        const bucket_id = keys_array.find(key => parsed[key] === "bucket");


                        // Check if the bucket id is in use
                        const exists = await frappe.db.exists("Bucket QR Code", bucket_id);

                        let bkt_id_doc;

                        if (exists) {
                            bkt_id_doc = await frappe.db.get_doc("Bucket QR Code", bucket_id);

                            if (bkt_id_doc.status === "In Use") {
                                frappe.msgprint(`Bucket ID ${bucket_id} is already In Use.`);
                                setTimeout(() => {
                                    frappe.set_route('Form', frm.doctype, 'new');
                                }, 2000);

                                setTimeout(() => {
                                    window.location.reload();
                                }, 1000);
                                return;
                            }
                        } else {
                            // Create new Bucket QR Code doc with ID = bucket_id
                            await frappe.call({
                                method: "frappe.client.insert",
                                args: {
                                    doc: {
                                        doctype: "Bucket QR Code",
                                        id: bucket_id,
                                        status: "Available"  // or your default status
                                    }
                                },
                                callback: function (r) {
                                    if (!r.exc) {
                                        console.log(`Created new Bucket QR Code: ${bucket_id}`);
                                    }
                                }
                            });
                        }



                        frm.set_value('bucket_id', bucket_id);
                        frm.refresh_fields(['item_code', 'bucket_id']);
                        html5QrCode.stop();
                        dialog.hide();


                    } catch (e) {
                        frappe.msgprint("Scanned a Non-Harvesting Label. Please scan a valid Bucket label.")
                        setTimeout(() => {
                            frappe.set_route('Form', frm.doctype, 'new');
                        }, 2000);

                        setTimeout(() => {
                            window.location.reload();
                        }, 1000);
                        console.error("Error parsing QR Code:", error)
                    }

                    html5QrCode.stop();
                    dialog.hide();
                },
                (errorMessage) => {
                    console.warn("Scanning error:", errorMessage);
                }
            ).catch((err) => {
                console.error("Error starting QR scanner:", err);
                frappe.msgprint("Unable to access the camera. Please ensure permissions are granted and try again.");
            });

            dialog.on("hide", function () {
                html5QrCode.stop().then(() => {
                    console.log("QR Scanner stopped.");
                }).catch((err) => {
                    console.error("Error stopping QR scanner:", err);
                });
            });
        }

        // Automatically start scanning when farm and block are filled
        frm.fields_dict.farm.df.onchange = checkAndStartScanner;
        frm.fields_dict.block.df.onchange = checkAndStartScanner;

    }
});

