async function process_grading_bunch_scan(bunchLabelData, farm, action, frm, audio) {

    try {
        // Get the stock entry with the bunch Id
        // Check if bunch is scanned via the custom_scanned_grading field
        const existingEntry = await frappe.db.get_list('Stock Entry', {
            fields: ['name', 'custom_scanned_grading'],
            filters: {
                custom_bunch_id: bunchLabelData.bunch_id
            },
            limit: 1
        });


        // Check if bunch is already scanned
        if (existingEntry.length > 0 && existingEntry[0].custom_scanned_grading === 1) {
            frappe.throw('Bunch has already been scanned.');
            return;
        }

        // Create a new stock entry
        const stockEntry = frappe.model.get_new_doc('Stock Entry');

        stockEntry.stock_entry_type = 'Grading';

        // Get the mapping for the farm
        const farm_mapping_doc = await frappe.db.get_doc("Scan Location Mapping", `${farm}-MAP`);
        const mapping_items = farm_mapping_doc.items;

        const row_array = mapping_items.filter(item => item.action == action)

        stockEntry.from_warehouse = row_array[0].source_warehouse;
        stockEntry.to_warehouse = row_array[0].target_warehouse;

        const farm_doc = await frappe.db.get_doc("Farm", farm);
        const company = farm_doc.company;

        stockEntry.custom_farm = farm;
        stockEntry.company = company;
        stockEntry.custom_business_unit = "Roses";

        stockEntry.custom_graded_by = frm.doc.grader;
        stockEntry.custom_stem_length = bunchLabelData.stem_length;
        stockEntry.custom_bunched_by = bunchLabelData.bunch_size;
        stockEntry.custom_bunch_id = bunchLabelData.bunch_id;
        stockEntry.custom_scanned_grading = 1;

        stockEntry.items = [{
            item_code: bunchLabelData.variety,
            uom: bunchLabelData.bunch_size,
            qty: 1
        }];

        await frappe.db.insert(stockEntry);
        audio.play();
        frappe.msgprint('Stock transfered successfully.');
    } catch (error) {

        frappe.throw(__('Error creating Stock Entry: ') + error.message);


    }

}

