async function process_bunch_scan(opl_url, bunchLabelData, farm, action, frm, audio) {

    const oplName = getNameFromUrl(opl_url);

    const bunchData = JSON.parse(bunchLabelData)

    const existingEntries = await frappe.db.get_list('Stock Entry', {
        fields: ['name', 'custom_bunch_id'],
        filters: {
            custom_bunch_id: bunchData.bunch_id
        },
        limit: 1
    });



    // If no matching stock entry is found
    if (existingEntries.length === 0) {
        frappe.throw(`This bunch has not been graded in the system. Perform the grading scan on it to enable packing: ${bunchData.bunch_id}`);
    }
    const bunchStockEntryName = existingEntries[0].name;
    const stockEntryUrl = `/app/stock-entry/${bunchStockEntryName}`;
    let bunchName;
    let previouslyScannedItems;
    let previously_scanned_items_list

    let totalBunches = 0;
    let scannedBunches = 0;

    let stockEntryDoc;


    try {
        stockEntryDoc = await frappe.db.get_doc('Stock Entry', bunchStockEntryName);
    } catch (error) {
        frappe.throw(`Failed to fetch Stock Entry: ${error}`);
    }

    if (stockEntryDoc.custom_scanned_packing ) {
        frappe.msgprint("Bunch aready scanned!");
        return;
    }

    // frappe.call({
    //     method: "upande_kaitet.server_scripts.update_custom_scanned.update_custom_scanned",
    //     args: { stock_entry_name: stockEntryName }
    // });

    // returns an object with total items and scanned items
    let varietyMatch = {};


    try {
        varietyMatch = await validateLabels(oplName, bunchLabelData, previouslyScannedItems, frm);
        bunchName = bunchData.variety;

        if (!varietyMatch.valid) {
            frappe.throw(`${bunchName} does not match the Order Pick List!`);
        }
    } catch (error) {
        frappe.throw(`Error validating labels: ${error}`);
        return;
    }

    const box_id = varietyMatch.box_number;
    const box_label = varietyMatch.box_label;

    // Add bunch detail to scanned_items child table
    const newBunchDetails = {
        variety: bunchData.variety,
        bunch_size: bunchData.bunch_size,
        number_of_bunches: 1,
    };


    totalBunches = frm.doc.total_bunches;
    totalScanned = varietyMatch.scannedItems + 1;

    frm.set_value('scanned_bunches', totalScanned);
    frappe.msgprint(`${totalScanned} of ${totalBunches} scanned`)


    await createOrGetFarmPackList(oplName, bunchStockEntryName, box_id, box_label, totalBunches);

    // Mark the bunch as scanned
    frappe.call({
        method: "upande_kaitet.server_scripts.update_custom_scanned.update_custom_scanned",
        args: { 
            stock_entry_name: bunchStockEntryName,
            action: action,
            oplName: oplName,
        }
    });

    audio.play();
    frm.save();

}


