async function createStockEntry(scan_value, farm, action, audio) {



    let variety;
    let qty;
    let grower;
    let uom;
    let harvester;
    let greenhouse;
    let block__bed_number;
    let stockEntryDoc;
    let buckt_id;

    // Use the stockEntryName to get the stock_entry_data field data needed for
    // stock transfer
    if (action == "Receiving") {
        try {
            const parsed = JSON.parse(scan_value);
            const keys_array = Object.keys(parsed);
            const bucket_id = keys_array.find(key => parsed[key] === "bucket");



            const bkt_id_doc = await frappe.db.get_doc("Bucket QR Code", bucket_id);

            if (bkt_id_doc.status == "Available") {

                frappe.throw(`Bucket ID ${bucket_id} is empty.`);

            }

            stockEntryDoc = await frappe.db.get_doc('Stock Entry', bkt_id_doc.last_stock_entry);

            if (stockEntryDoc.custom_scanned ) {
                frappe.throw("Bucket already scanned!");

                return;
            }

            const items = stockEntryDoc.items;
            greenhouse = stockEntryDoc.custom_greenhouse;
            harvester = stockEntryDoc.custom_harvester_payroll_number;
            block__bed_number = stockEntryDoc.custom_block__bed_number;
            buckt_id = bucket_id;

            if (Array.isArray(items) && items.length > 0) {
                variety = items[0].item_code;
                qty = items[0].qty;
            }


            frappe.msgprint(`Harvester: ${harvester}, Greenhouse: ${greenhouse}, Bay: ${block__bed_number}, No. of Stems: ${qty} `)

        } catch (error) {
            if (error.includes("Bucket already scanned!")) {
                console.log(error);
            }
            frappe.throw(`Failed to fetch Stock Entry: ${error}`);
        }


    }

    if (action === "Packing") {
        const stockEntryName = scan_value;

        try {
            stockEntryDoc = await frappe.db.get_doc('Stock Entry', stockEntryName);

        } catch (error) {
            frappe.throw(`Failed to fetch Stock Entry: ${error}`);
        }

        const items = stockEntryDoc.items;

        items.forEach((item) => {
            variety = item.item_code;

            // Flowers are packed per 1 bunch
            qty = 1;
            uom = item.uom;
        });


    }

    if (action === 'Receiving Quarantined') {
        try {
            const parsed = JSON.parse(scan_value);
            const keys_array = Object.keys(parsed);
            const bucket_id = keys_array.find(key => parsed[key] === "bucket");

            const bkt_id_doc = await frappe.db.get_doc("Bucket QR Code", bucket_id);

            if (bkt_id_doc.status == "Available") {

                frappe.throw(`Bucket ID ${bucket_id} is empty.`);

            }

            stockEntryDoc = await frappe.db.get_doc('Stock Entry', bkt_id_doc.last_stock_entry);

            if (stockEntryDoc.custom_scanned ) {
                frappe.throw("Bucket already scanned!");
                return;
            }

            const items = stockEntryDoc.items;

            greenhouse = stockEntryDoc.custom_greenhouse;
            harvester = stockEntryDoc.custom_harvester;
            block__bed_number = stockEntryDoc.custom_block__bed_number;
            buckt_id = bucket_id;

            if (Array.isArray(items) && items.length > 0) {
                variety = items[0].item_code;
                qty = items[0].qty;
            }

            frappe.msgprint(`Harvester: ${harvester}, Greenhouse: ${greenhouse}, Bay: ${block__bed_number}, No. of Stems: ${qty} `)

        } catch (error) {
            if (error.includes("Bucket already scanned!")) {
                console.log(error);
            }

            frappe.throw(`Failed to fetch Stock Entry: ${error}`);
        }


    }

    const stock_entry_type_mapping = {
        "Receiving": "Receiving",
        "Receiving Quarantined": "Receiving Quarantined",
    };

    const stock_entry_type = stock_entry_type_mapping[action];

    // Get the mapping for the farm
    const farm_mapping_doc = await frappe.db.get_doc("Scan Location Mapping", `${farm}-MAP`);
    const mapping_items = farm_mapping_doc.items;

    const row_array = mapping_items.filter(item => item.action == action);

    // Check current time to determine target warehouse
    const currentTime = new Date();
    const currentHour = currentTime.getHours();
    const is_after_2pm = currentHour >= 14; // 14:00 is 2 PM in 24-hour format

    // Determine target warehouse based on time
    let target_warehouse;
    if (is_after_2pm) {
        target_warehouse = `${farm} Holding Store - KR`;
    } else {
        target_warehouse = row_array[0].target_warehouse;
    }

    // Change the mapping of the location to check the location doctype 
    const locationMapping = {
        [`${farm} Receiving`]: { source: `${greenhouse}`, target: target_warehouse },
        [`${farm} Receiving Quarantined`]: { source: `${greenhouse}`, target: target_warehouse },
    };

    const scanLocation = `${farm} ${action}`;

    const stockmvt = locationMapping[scanLocation];

    const farm_doc = await frappe.db.get_doc("Farm", farm);
    const company = farm_doc.company;

    if (!stockmvt) {
        frappe.msgprint(`Invalid scan location: ${scanLocation}`);
        return;
    }

    stock_entry_data = {
        "location data": stockmvt,
        "variety": variety,
        "quantity": qty,
        "grower": grower,
        "harvester": harvester,
        "greenhouse": greenhouse,
        "stock entry type": stock_entry_type,
        "uom": uom,
        "block__bed_number": block__bed_number,
        "bucket_id": buckt_id,
        "farm": farm,
        "business_unit": "Roses",
        "company": company
    };



    const created_stock_entry = await frappe.call({
        method: "upande_kaitet.server_scripts.create_stock_entry.create_stock_entry",
        args: {
            stock_entry_data: JSON.stringify(stock_entry_data),
        }
    });

    if (!created_stock_entry.message || !created_stock_entry.message.includes("SE")) {
        frappe.throw("Stock Entry Failed");
    }

    if ( created_stock_entry.message.includes("SE") && (stock_entry_type == "Receiving" || stock_entry_type == "Receiving Quarantined") ) {
        await frappe.call({
            method: "upande_kaitet.server_scripts.update_custom_scanned.update_custom_scanned",
            args: { 
                stock_entry_name: stockEntryDoc.name,
                action: action,
                oplName: null
            }
        });

        audio.play();
    }

    return created_stock_entry;

}

