function create_delivery_note(frm) {

    frappe.model.with_doctype('Delivery Note', function() {
        let delivery_note = frappe.model.get_new_doc('Delivery Note');

        delivery_note.company = frm.doc.company;
        delivery_note.posting_date = frappe.datetime.get_today();
        delivery_note.set_posting_time = 0;
        delivery_note.material_request = frm.doc.name;
        delivery_note.customer = frm.doc.customer || 'Yoghurt Dispatch';
        delivery_note.custom_farm = frm.doc.custom_farm;
        delivery_note.custom_business_unit = frm.doc.custom_business_unit;
        delivery_note.custom_location = 'Ravine';
        delivery_note.custom_delivery_type = 'Yoghurt Dispatch';


        // Map items from Material Request to Delivery Note
        frm.doc.items.forEach(function(item) {
            let delivery_item = frappe.model.add_child(delivery_note, 'items');
            delivery_item.item_code = item.item_code;
            delivery_item.item_name = item.item_name;
            delivery_item.qty = item.qty;
            delivery_item.uom = item.uom;
            delivery_item.warehouse = item.warehouse;
            delivery_item.material_request = frm.doc.name;
            delivery_item.material_request_item = item.name;
        });

        // Open the new Delivery Note for editing
        frappe.set_route('Form', 'Delivery Note', delivery_note.name);

        frappe.msgprint(__("Delivery Note created. Please complete the remaining details and save."));
    });
}

