    if (user_roles.includes('Karen Store Manager')) {
        if (!frm.doc.karen_store_approved) {
            frm.add_custom_button(__('Approve - Karen Store'), function() {
                approve_delivery(frm, 'karen_store_approved', 'Karen Store');
            }).addClass('btn-success');
        } else {
            frm.add_custom_button(__(' Karen Store Approved'), function() {
                frappe.msgprint('Already approved by Karen Store');
            }).addClass('btn-default').attr('disabled', true);
        }
    }

    // Karen Security Approval Button
    if (user_roles.includes('Karen Security Officer')) {
        if (!frm.doc.karen_security_approved) {
            frm.add_custom_button(__('Approve - Karen Security'), function() {
                approve_delivery(frm, 'karen_security_approved', 'Karen Security');
            }).addClass('btn-success');
        } else {
            frm.add_custom_button(__(' Karen Security Approved'), function() {
                frappe.msgprint('Already approved by Karen Security');
            }).addClass('btn-default').attr('disabled', true);
        }
    }

    // Reject Button (available to all approvers and System Manager)
    if (user_roles.includes('System Manager') || 
        user_roles.includes('Kapkolia Store Manager') ||
        user_roles.includes('Kapkolia Security Officer') ||
        user_roles.includes('Karen Store Manager') ||
        user_roles.includes('Karen Security Officer')) {

        frm.add_custom_button(__('Reject'), function() {
            reject_delivery(frm);
        }).addClass('btn-danger');
    }
}

frappe.ui.form.on('Sales Order', {
    delivery_date: function(frm) {
        if (frm.doc.delivery_date) {
            let date = new Date(frm.doc.delivery_date);
            let week_number = getWeekNumber(date);
            frm.set_value('custom_week', week_number);
        }
    }
});

function getWeekNumber(date) {
    // Clone the date to avoid modifying the original
    let d = new Date(Date.UTC(date.getFullYear(), date.getMonth(), date.getDate()));

    // Set to nearest Thursday: current date + 4 - current day number
    // Make Sunday's day number 7
    d.setUTCDate(d.getUTCDate() + 4 - (d.getUTCDay() || 7));

    // Get first day of year
    let yearStart = new Date(Date.UTC(d.getUTCFullYear(), 0, 1));

    // Calculate full weeks to nearest Thursday
    let weekNo = Math.ceil((((d - yearStart) / 86400000) + 1) / 7);

    return weekNo;
}
