def get_manufacturing_managers():
    user_ids = frappe.get_all("Has Role", filters={"role": "Manufacturing Manager"}, pluck="parent")

    if not user_ids:
        return []

    return frappe.get_all(
        "Employee",
        filters={
            "user_id": ["in", user_ids],
            "status": "Active"
        },
        pluck="name"
    )

def execute():
    try:
        vehicles = frappe.get_all("Vehicle", filters={"imei": ["!=", ""]}, fields=["name", "imei"])

        for v in vehicles:
            # Log which vehicle we're checking
            frappe.logger().info(f"üîç Checking {v.name} (IMEI: {v.imei})")

            readings = frappe.get_all(
                "GPS Reading",
                filters={
                    "imei": v.imei,
                    "latitude": ["not in", [None, "", 0]],
                    "longitude": ["not in", [None, "", 0]]
                },
                fields=["latitude", "longitude", "creation"],
                order_by="creation desc",
                limit=1
            )

            if readings:
                lat = float(readings[0]["latitude"])
                lon = float(readings[0]["longitude"])
                ts = readings[0]["creation"]

                frappe.db.set_value("Vehicle", v.name, {
                    "latitude": lat,
                    "longitude": lon,
                    "timestamp": ts
                })

                frappe.logger().info(f"‚úÖ Updated {v.name}: lat={lat}, lon={lon}, ts={ts}")
            else:
                frappe.logger().info(f"‚õî No GPS data for {v.name} (IMEI: {v.imei})")

        frappe.db.commit()

    except Exception:
        frappe.log_error(frappe.get_traceback(), "‚ùå Vehicle GPS update failed")
        raise
