script:
if frappe.db.exists("Sales Invoice", doc.name):
    inv_doc = frappe.get_doc(
        "Sales Invoice",
        doc.name,
    )

    original_posting_date = inv_doc.get("posting_date")
    original_posting_time = inv_doc.get("posting_time")
    original_due_date = inv_doc.get("due_date")


    if inv_doc:

        if original_posting_date:
            doc.posting_date = original_posting_date

        if original_posting_time:
            doc.posting_time = original_posting_time

        if original_due_date:
            doc.due_date = original_due_date

if doc.stock_entry_type == "Harvesting":
    if not doc.custom_bucket_id:
        frappe.throw(f"Bucket ID needed for {doc.stock_entry_type} Stock Entry")


    if doc.get("__islocal") or doc.has_value_changed("custom_bucket_id"):
        existing = frappe.db.sql("""
            SELECT name FROM `tabStock Entry` 
            WHERE custom_bucket_id = %s AND name != %s
        """, (doc.custom_bucket_id, doc.name or ""), as_dict=1)

        if existing:
            frappe.throw(f"The Bucket ID '{doc.custom_bucket_id}' is already in use. Please choose a unique value.")



elif doc.stock_entry_type == "Receiving" or doc.stock_entry_type == "Receiving Quarantined":
    if not doc.custom_received_bucket_id:
        frappe.throw(f"Bucket ID needed for {doc.stock_entry_type} Stock Entry")

    if doc.get("__islocal") or doc.has_value_changed("custom_received_bucket_id"):
        existing = frappe.db.sql("""
            SELECT name FROM `tabStock Entry` 
            WHERE custom_received_bucket_id = %s AND name != %s
        """, (doc.custom_received_bucket_id, doc.name or ""), as_dict=1)

        if existing:
            frappe.throw(f"The Bucket ID '{doc.custom_received_bucket_id}' is already in use. Please choose a unique value.")

def get_manufacturing_managers():
    user_ids = frappe.get_all("Has Role", filters={"role": "Manufacturing Manager"}, pluck="parent")

    if not user_ids:
        return []

    return frappe.get_all(
        "Employee",
        filters={
            "user_id": ["in", user_ids],
            "status": "Active"
        },
        pluck="name"
    )

try:
    # First check content type
    if not frappe.request.content_type or 'application/json' not in frappe.request.content_type:
        frappe.throw(
            "Request must be JSON with Content-Type: application/json",
            exc=frappe.InvalidContentType
        )

    data = frappe.request.get_json()

    # Validate required fields
    required_fields = ['longitude', 'latitude']
    for field in required_fields:
        if field not in data:
            frappe.throw(f"Missing required field: {field}")

    # Create document
    doc = frappe.get_doc({
        "doctype": "GPS Readings",
        "geolocation_zwck": f"{data['latitude']},{data['longitude']}",
        "table_dgzd": [{
            "longitude": data['longitude'],
            "latitude": data['latitude'],
            "timestamp": data.get('timestamp'),
            "imei": data.get('imei', ''),
            "date": data.get('date')
        }]
    }).insert()

    frappe.response.update({
        "status": "success",
        "message": "GPS Reading created successfully",
        "data": {
            "name": doc.name,
            "location": doc.geolocation_zwck
        }
    })

except frappe.InvalidContentType:
    frappe.response.update({
        "status": "error",
        "message": "Invalid content type - please send with Content-Type: application/json"
    })
    frappe.response.http_status_code = 415

except Exception as e:
    frappe.log_error(title="GPS API Error", message=str(e)[:130])
    frappe.response.update({
        "status": "error",
        "message": "Failed to process request"
    })
    frappe.response.http_status_code = 500

# Trigger on Work Order submit
if not doc.custom_weighed_amount:
    frappe.throw("Please enter the Weighed Amount before submitting.")

# Fetch the final stock entry linked to this Work Order
stock_entry = frappe.get_all(
    "Stock Entry",
    filters={"work_order": doc.name, "purpose": "Manufacture"},
    fields=["name"]
)

if stock_entry:
    se = frappe.get_doc("Stock Entry", stock_entry[0]["name"])

    for item in se.items:
        if item.is_finished_item:
            weighed_qty = doc.custom_weighed_amount

            # Convert if needed
            if item.stock_uom != doc.fg_uom:
                conversion_factor = frappe.get_value("UOM Conversion Detail", {
                    "parent": item.item_code,
                    "uom": doc.fg_uom
                }, "conversion_factor")

                if not conversion_factor:
                    frappe.throw(f"No UOM conversion defined from {doc.fg_uom} to {item.stock_uom} for item {item.item_code}")

                weighed_qty = weighed_qty * conversion_factor

            item.qty = weighed_qty
            item.transfer_qty = weighed_qty

    se.save()
    frappe.msgprint(f"Updated Stock Entry {se.name} with weighed quantity.")

def request_concession(quality_inspection_name):
    try:
        # Log the initial step
        frappe.logger().info(f"Requesting concession for {quality_inspection_name}")

        # Update the Quality Inspection document
        result = frappe.db.set_value("Quality Inspection", quality_inspection_name, {
            "status": "Accepted",
            "concession_requested": 1
        })

        # Log the result of the update
        frappe.logger().info(f"Update result: {result}")

        frappe.msgprint(f"Concession requested successfully for {quality_inspection_name}. Status updated to Accepted.")
        return {"success": True, "message": f"Updated {quality_inspection_name}"}
    except Exception as e:
        frappe.log_error(message=str(e), title="Concession Request Error")
        return {"success": False, "message": f"Error: {str(e)}"}

if doc.stock_entry_type == "Grading":
    name = doc.name

    if frappe.db.exists("QR Code", name):
        frappe.delete_doc("QR Code", name)

    if doc.custom_bunch_qr_code:
        qr_code_doc = frappe.get_doc({
            "doctype": "QR Code",  
            "stock_entry": doc.name,
            "qr_code_image": doc.custom_bunch_qr_code,  
        })
        qr_code_doc.insert()

if doc.stock_entry_type == "Grading Forecast":
    farm = doc.custom_farm
    new_tracker_doc = None
    varieties_in_stock_entry = []

    for item in doc.items:
        if item.t_warehouse:
            varieties_in_stock_entry.append({
                "item_code": item.item_code,
                "qty": item.qty
            })


    tracker_list = frappe.get_list("Grading Forecast Tracker", fields='name',
        filters= {
            'farm': farm
        })

    if len(tracker_list) < 1:
        new_tracker_doc = frappe.new_doc("Grading Forecast Tracker")

        new_tracker_doc.farm = farm
        new_tracker_doc.insert()

        for item in varieties_in_stock_entry:
            item_found = False

            for tracker_item in new_tracker_doc.items:
                if tracker_item.variety == item["item_code"]:
                    tracker_item.qty_deficit_in_forecast = tracker_item.qty_deficit_in_forecast + item["qty"]
                    item_found = True
                    break

            if not item_found:
                new_tracker_doc.append("items", {
                    "variety": item["item_code"],
                    "qty_deficit_in_forecast": item["qty"]
                })

        new_tracker_doc.save()

    if len(tracker_list) > 0:
        existing_tracker = frappe.get_doc("Grading Forecast Tracker", farm)

        for item in varieties_in_stock_entry:
            item_found = False

            for tracker_item in existing_tracker.items:
                if tracker_item.variety == item["item_code"]:
                    tracker_item.qty_deficit_in_forecast = tracker_item.qty_deficit_in_forecast + item["qty"]
                    item_found = True
                    break

            if not item_found:
                new_item_row = existing_tracker.append("items", {
                    "variety": item["item_code"],
                    "qty_deficit_in_forecast": item["qty"]
                })

        # After updating items, save the existing tracker
        existing_tracker.save()

if doc.items:
    for item in doc.items:
        if item.sales_order_id:
            doc.custom_sales_order_id_cpl = item.sales_order_id
            break

if doc.stock_entry_type == "Grading":
    # Remove the variety and quantity to go and pay for the debt in forecast while
    variety_in_stock_entry = []
    farm = doc.custom_farm

    abbr_obj = {
        "Burguret": "BUR",
        "Pendekeza": "PNDK",
        "Turaco": "TUR",
    }

    for item in doc.items:
        variety_in_stock_entry.append({
            "item_code": item.item_code,
            "qty": item.transfer_qty
        })

    existing_tracker = frappe.get_doc("Grading Forecast Tracker", farm)
    items_to_transfer = []

    for item in variety_in_stock_entry:
        item_found = False

        # Loop through existing tracker items to find a match by item_code
        for tracker_item in existing_tracker.items:
            if tracker_item.variety == item["item_code"]:
                # Check the balance(qty) in the tracker and pay that, maximum of the qty of stems graded in the stock entry
                item_found = True
                if tracker_item.qty_deficit_in_forecast != 0:
                    deficit = abs(tracker_item.qty_deficit_in_forecast)
                    transferable_qty = min(item["qty"], deficit)

                    # Adjust the deficit in the tracker (always move toward zero)
                    if tracker_item.qty_deficit_in_forecast > 0:
                        tracker_item.qty_deficit_in_forecast = tracker_item.qty_deficit_in_forecast - transferable_qty
                    else:
                        tracker_item.qty_deficit_in_forecast = tracker_item.qty_deficit_in_forecast + transferable_qty

                    # Append the item to the transfer list
                    items_to_transfer.append({
                        "source_item": tracker_item.variety,
                        "target_item": f"{abbr_obj.get(farm)} Forecast Item",
                        "qty": transferable_qty
                    })

                break


        # If item_code wasn't found, create a new row in the tracker item table
        if not item_found:
            break

        # After updating items, save the existing tracker
        existing_tracker.save()

    if len(items_to_transfer) > 0:
        # Perform a stock entry called type Forecast Repayment that is repack of variety from {farm} Available for Sale to
        # {farm} Grading Forecast - TL as variety {farm} Grading Forecast
        stock_entry = frappe.new_doc("Stock Entry")
        stock_entry.stock_entry_type = "Forecast Repayment"

        for transfer_item in items_to_transfer:
            stock_entry.append("items", {
                "item_code": transfer_item["source_item"],  # Source: actual variety
                "qty": transfer_item["qty"],
                "s_warehouse": f"{farm} Available for Sale - TL"
            })
            stock_entry.append("items", {
                "item_code": transfer_item["target_item"],  # Target: Graded Forecast item
                "qty": transfer_item["qty"],
                "t_warehouse": f"{abbr_obj.get(farm)} Grading Forecast - TL"
            })

        stock_entry.insert()
        stock_entry.submit()

        doc.db_set("custom_fc_repayment_doc", stock_entry.name)

if doc.stock_entry_type == "Grading Forecast":
    reversed_items = []
    tracker_doc = frappe.get_doc("Grading Forecast Tracker", doc.custom_farm)

    for item in doc.items:
        if item.t_warehouse:
            reversed_items.append({
                "item_code": item.item_code,
                "qty": item.qty
            })


    for item in tracker_doc.items:
        for i in reversed_items:
            if item.variety == i["item_code"]:
                item.qty_deficit_in_forecast = item.qty_deficit_in_forecast - i["qty"]
                break

    tracker_doc.save()

stock_entries = frappe.get_all("Stock Entry", filters={"custom_opl_scanned": doc.name}, fields=["name"])

for entry in stock_entries:
    se_doc = frappe.get_doc("Stock Entry", entry.name)

    if se_doc.docstatus == 1:
        se_doc.db_set("custom_scanned_packing", 0)

customer_doc = frappe.get_doc("Customer", doc.customer)

if not customer_doc.is_internal_customer:
    # Create sales invoice
    new_doc = frappe.new_doc("Sales Invoice")
    new_doc.customer = doc.customer
    new_doc.custom_farm = doc.custom_farm
    new_doc.custom_business_unit = doc.custom_business_unit
    new_doc.custom_delivery_note_no = doc.name
    new_doc.company = "Karen Roses"

    items_array = doc.items

    for item in items_array:
        new_doc.append("items", {
            "item_code": item.item_code,
            "uom": item.uom,
            "qty": item.qty,
            "rate": item.rate,
            "custom_length": item.custom_length,
        })


    new_doc.save()

    # Create a beautiful clickable message with black theme
    invoice_link = f"/app/sales-invoice/{new_doc.name}"

    frappe.msgprint(
        msg=f"""
        <div style="text-align: center; padding: 24px; background: linear-gradient(135deg, #1a1a1a 0%, #2d2d2d 50%, #1a1a1a 100%); 
                    border-radius: 16px; color: #ffffff; font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
                    border: 1px solid #333333; box-shadow: 0 20px 40px rgba(0,0,0,0.3);">
            <div style="font-size: 28px; margin-bottom: 12px; color: #00ff88;">
                 Success!
            </div>
            <div style="font-size: 16px; margin-bottom: 24px; color: #e0e0e0; font-weight: 400;">
                Sales Invoice has been created successfully
            </div>
            <a href="{invoice_link}" 
               style="display: inline-block; padding: 14px 28px; background: linear-gradient(135deg, #333333 0%, #4a4a4a 100%); 
                      color: #ffffff; text-decoration: none; border-radius: 30px; font-weight: 600; 
                      border: 2px solid #555555; transition: all 0.3s ease;
                      box-shadow: 0 8px 16px rgba(0,0,0,0.2);
                      backdrop-filter: blur(10px);"
               onmouseover="this.style.background='linear-gradient(135deg, #00ff88 0%, #00cc6a 100%)'; 
                           this.style.transform='translateY(-3px)'; 
                           this.style.boxShadow='0 12px 24px rgba(0,255,136,0.2)';
                           this.style.borderColor='#00ff88';"
               onmouseout="this.style.background='linear-gradient(135deg, #333333 0%, #4a4a4a 100%)'; 
                          this.style.transform='translateY(0px)'; 
                          this.style.boxShadow='0 8px 16px rgba(0,0,0,0.2)';
                          this.style.borderColor='#555555';">
                 View Invoice {new_doc.name}
            </a>
            <div style="margin-top: 16px; font-size: 12px; color: #888888; opacity: 0.8;">
                Click the button above to open the invoice
            </div>
        </div>
        """,
        title="Invoice Created",
        indicator="green"
    )

    new_doc.submit()
